import React, { useState } from 'react';

const ToDoForm = ({ addNewTask }) => {  // Ensure addNewTask is received as a prop
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [category, setCategory] = useState('General');
  const [deadline, setDeadline] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const taskData = {
      description,
      priority,
      category,
      deadline,
      completed: false  // Set a default completed status
    };

    console.log('Submitting task:', taskData);

    // Send the task to the API
    fetch('http://127.0.0.1:5000/api/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(taskData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('Success:', data);

        // Check if addNewTask is a function and call it
        if (typeof addNewTask === 'function') {
          console.log('addNewTask is a function, updating task list...');
          addNewTask(taskData);  // Update the task list with the new task
        } else {
          console.error('addNewTask is not a function');
        }

        // Clear the form after successful submission
        setDescription('');
        setPriority('Medium');
        setCategory('General');
        setDeadline('');
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Task description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
       <div>
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
      </div>
      <input
        type="text"
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <input
        type="date"
        value={deadline}
        onChange={(e) => setDeadline(e.target.value)}
      />
      <button type="submit">Add Task</button>
    </form>
  );
};

export default ToDoForm;
