import React, { useState } from 'react';

const ToDoForm = ({ addNewTask }) => {
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [category, setCategory] = useState('General');
  const [deadline, setDeadline] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const taskData = {
      description,
      priority,
      category,
      deadline,
      completed: false  // Set a default completed field
    };

    // Send the task to the API
    fetch('http://127.0.0.1:5000/api/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(taskData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log('API Response:', data);  // Log the API response to confirm submission

        // If the task was successfully added to the server, update the list
        if (data.status === 'success') {
          addNewTask(taskData);  // Pass the new task to the parent component to update the list
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    // Clear the form after submission
    setDescription('');
    setPriority('Medium');
    setCategory('General');
    setDeadline('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Task description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <div>
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="High">High</option>
          <option value="Medium">Medium</option>
          <option value="Low">Low</option>
        </select>
      </div>
      <input
        type="text"
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <input
        type="date"
        value={deadline}
        onChange={(e) => setDeadline(e.target.value)}
      />
      <button type="submit">Add Task</button>
    </form>
  );
};

export default ToDoForm;
