import React, { useState, useEffect } from 'react';
import ToDoForm from './ToDoForm';  // Make sure this path is correct

const ToDoList = () => {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks when the component mounts
  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/tasks')
      .then((response) => response.json())
      .then((data) => {
        setTasks(data.tasks);  // Assuming data.tasks is the correct response structure
      })
      .catch((error) => console.error('Error fetching tasks:', error));
  }, []);

  // Function to add a new task dynamically after it's submitted
  const addNewTask = (newTask) => {
    setTasks((prevTasks) => [...prevTasks, newTask]);  // Update the state with the new task
  };

  return (
    <div>
      <h2>Task List</h2>
      {/* Pass the addNewTask function to the ToDoForm component */}
      <ToDoForm addNewTask={addNewTask} />
      {tasks.length > 0 ? (
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              <strong>{task.description}</strong> 
              (Priority: {task.priority}, 
              Category: {task.category}, 
              Deadline: {task.deadline ? task.deadline : 'No deadline'}) 
              {task.completed ? " [✓]" : " [✗]"}
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default ToDoList;
