import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';  // Import socket.io-client

const ToDoList = () => {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the backend
  useEffect(() => {
    const socket = io('http://127.0.0.1:5000');

    fetch('http://127.0.0.1:5000/api/tasks')
      .then(response => response.json())
      .then(data => setTasks(data.tasks))
      .catch(error => console.error('Error fetching tasks:', error));

    socket.on('task_update', (newTask) => {
      setTasks(prevTasks => [...prevTasks, newTask]);
    });

    socket.on('task_deleted', ({ task_id }) => {
      setTasks(prevTasks => prevTasks.filter((task, index) => index !== task_id));
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  // Function to delete a task
  const deleteTask = (task_id) => {
    fetch(`http://127.0.0.1:5000/api/tasks/${task_id}`, {
      method: 'DELETE',
    })
      .then(response => response.json())
      .then(data => {
        console.log('Task deleted:', data.message);
      })
      .catch(error => console.error('Error deleting task:', error));
  };

  return (
    <div>
      <h2>Task List</h2>
      {tasks.length > 0 ? (
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              <strong>{task.description}</strong>
              (Priority: {task.priority}, Category: {task.category}, Deadline: {task.deadline ? task.deadline : 'No deadline'})
              {task.completed ? " [✓]" : " [✗]"}
              {/* Button to delete a task */}
              <button onClick={() => deleteTask(task.id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default ToDoList;
