  const addNewTask = (newTask) => {
    console.log('PREVIOUS:', tasks);  // Log current tasks

    // Update the state and log the new task added to the state
    setTasks((prevTasks) => {
      const updatedTasks = [...prevTasks, newTask];  // Add new task to the array
      console.log('Updated tasks after adding:', updatedTasks);  // Log the updated tasks list
      return updatedTasks;  // Update the state
    });
  };



  return (
    <div>
      <h2>Task List hello</h2>
      {tasks.length > 0 ? (
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              <strong>{task.description}</strong> 
              (Priority: {task.priority}, 
              Category: {task.category}, 
              Deadline: {task.deadline ? task.deadline : 'No deadline'}) 
              {task.completed ? " [✓]" : " [✗]"}
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default ToDoList;
